import Restricciones from '../data/APIController.js'


export const useRestricciones = async () => {
    const datosRestricciones = await Restricciones();
    let restriccionesAdaptadas = [];

    datosRestricciones.forEach(restriccion => {
        restriccionesAdaptadas.push(
            {
                label: `${restriccion.id} : ${restriccion.abreviacion}`,
                value: `${restriccion.id} : ${restriccion.abreviacion}`
            }
            )
    });

    return restriccionesAdaptadas;
}
