import {useState} from 'react';
import {useRestricciones} from '../hooks/useRestricciones';
import Select from 'react-select';
import Restricciones from '../data/APIController.js'

export const FormRestricciones =  () => {
    const [arAsignarRestriccion,guardarAsignarRestriccion] = useState([]);
    const [datosRestricciones,guardarDatosRestricciones] = useState([]);
    let datos = []
    datos.push(Restricciones().then(data => { return data}));
    let datosParseados =  [];
    datos.forEach(restriccion => {
        datosParseados.push(
            {
                 label :`${restriccion.id} : ${restriccion.abreviacion}`,
                 value: `${restriccion.id} : ${restriccion.abreviacion}`
            });
    })
    console.log(datos);
    return (
        <>
        <div>
            <Select 
                isMulti
                options={datosParseados}
                defaultValue={arAsignarRestriccion}
                onChange={(e) => guardarAsignarRestriccion(e)}
            />
            <button>Asignar restriccion</button> 
        </div>       
        </>
    )
}
export default FormRestricciones;
