import {useState} from 'react';
import {useRestricciones} from '../hooks/useRestricciones';
import Select from 'react-select';

export const FormRestricciones = () => {
    const [arAsignarRestriccion,guardarAsignarRestriccion] = useState([]);
    const restricciones = useRestricciones();
    return (
        <>
        <Select 
        isMulti
        options={restricciones} 
        defaultValue={arAsignarRestriccion}
        onChange={(e) => guardarAsignarRestriccion(e)}
        />
        <button onClick={console.log(arAsignarRestriccion)}>Asignar restriccion</button> 
        </>
    )
}
export default FormRestricciones;
